#include &lt;string&gt;

enum LogLevel {
    DEBUG,
    INFO,
    WARN,
    ERROR
};

class Logger {
public:
    static void log(LogLevel level, const char* format, ...);

private:
    static std::string getCurrentTime();
};
</code></pre> logger.cpp<pre><code>void Logger::log(LogLevel level, const char* format, ...) {
#ifndef DEBUG_LOG
    if (level == DEBUG) {
        return;
    }
#endif
    std::string levelStr;
    switch (level) {
        case DEBUG:
            levelStr = "DEBUG";
            break;
        case INFO:
            levelStr = "INFO";
            break;
        case WARN:
            levelStr = "WARN";
            break;
        case ERROR:
            levelStr = "ERROR";
            break;
    }

    std::string timeStr = getCurrentTime();
    std::string prefix = timeStr + " [" + levelStr + "] ";

    std::cout &lt;&lt; prefix;
    va_list args;
    va_start(args, format);
    std::vprintf(format, args);
    va_end(args);
    std::cout &lt;&lt; std::endl;
}

std::string Logger::getCurrentTime() {
    std::time_t now = std::time(nullptr);
    char buffer[100];
    std::strftime(buffer, sizeof(buffer), "%Y-%m-%d %H:%M:%S", std::localtime(&amp;now));
    return buffer;
}
</code></pre> <p>Example</p> <pre><code>int a;
int b;
Logger::log(DEBUG, "a=%d, b=%d", a, b);
</code></pre> mylogger.py<pre><code>import logging
from logging import handlers
import threading


class Log:
    _instance_lock = threading.Lock()

    def init(self, *args, **kwargs):
        # 日志等级 默认: INFO
        self.level = (kwargs["level"] if kwargs.get("level") else "INFO")
        # self.level = (kwargs["level"] if kwargs.get("level") else "DEBUG")
        # 时间戳格式 默认: %Y-%m-%d-%H:%M:%S
        self.datefmt = (kwargs["datefmt"] if kwargs.get("datefmt") else "%Y-%m-%d_%H:%M:%S")
        # 日志输出格式 默认: [时间] [模块名] [函数名] [日志等级] 进程号 线程号 打印内容
        self.format = logging.Formatter((kwargs["format"] if kwargs.get("format") else 
        "%(asctime)s.%(msecs)03d [%(module)s] [%(funcName)s] [%(levelname)s] %(process)d %(thread)d %(message)s"), self.datefmt)
        # 是否控制台输出 默认: 是
        self.console = (kwargs["console"] if kwargs.get("console") else True)
        # 日志文件名前缀 默认: log
        self.filename = "./logs/" + (kwargs["filename"] + "_" if kwargs.get("filename") else "") + "log" 
        # 日志切分时间 默认: 0点
        self.when = (kwargs["when"] if kwargs.get("when") else "MIDNIGHT")
        # 日志滚动保留个数 默认: 1
        self.backupCount = (kwargs["backupCount"] if kwargs.get("backupCount") else 1)

        self.level_map = {
            "DEBUG" : logging.DEBUG,
            "INFO"  : logging.INFO,
            "WARNING" : logging.WARNING,
            "ERROR" : logging.ERROR,
            "CRITICAL" : logging.CRITICAL
        }

        self.logger = logging.getLogger("__name__")
        self.logger.setLevel(self.level_map.get(self.level))

        # 控制台日志handler
        if self.console:
            stream_handler = logging.StreamHandler()
            stream_handler.setFormatter(self.format)
            self.logger.addHandler(stream_handler)

        # 文件日志handler
        file_handler = handlers.TimedRotatingFileHandler(
            filename=self.filename,
            when=self.when,
            backupCount=self.backupCount,
            delay = True,
            encoding="utf-8"
        )
        file_handler.setFormatter(self.format)
        self.logger.addHandler(file_handler)

    def __new__(cls, *args, **kwargs):
        if not hasattr(Log, "_instance"):
            with Log._instance_lock:
                if not hasattr(Log, "_instance"):
                    Log._instance = object.__new__(cls)
                    Log._instance.init(*args, **kwargs)
                    Log._instance.logger.debug("logger init success")
        return Log._instance

mylogger = Log().logger
</code></pre> <p>Example</p> <pre><code>from mylogger import mylogger

def test():
    mylogger.info("hello world")

if __name__ == "__main__":
    test()
</code></pre>

C++Python filesystemdirent.h <pre><code>#include &lt;iostream&gt;
#include &lt;filesystem&gt;

namespace fs = std::filesystem;

int main() {
    std::string path = ".";

    try {
        for (const auto&amp; entry : fs::directory_iterator(path)) {
            std::cout &lt;&lt; entry.path() &lt;&lt; std::endl;
        }
    } catch (const fs::filesystem_error&amp; e) {
        std::cerr &lt;&lt; "Filesystem error: " &lt;&lt; e.what() &lt;&lt; std::endl;
    } catch (const std::exception&amp; e) {
        std::cerr &lt;&lt; "General exception: " &lt;&lt; e.what() &lt;&lt; std::endl;
    }

    return 0;
}
</code></pre> <pre><code>#include &lt;iostream&gt;
#include &lt;dirent.h&gt;
#include &lt;cstring&gt;

int main() {
    const char *path = ".";
    DIR *dir = opendir(path);

    if (dir == nullptr) {
        std::cerr &lt;&lt; "Error opening directory: " &lt;&lt; std::strerror(errno) &lt;&lt; std::endl;
        return 1;
    }

    struct dirent *entry;
    while ((entry = readdir(dir)) != nullptr) {
        std::cout &lt;&lt; entry-&gt;d_name &lt;&lt; std::endl;
    }

    closedir(dir);
    return 0;
}
